import React, { useState, useRef, useCallback } from 'react';
import AudioUploader from './components/AudioUploader';
import Visualizer from './components/Visualizer';
import TemplateSelector from './components/TemplateSelector';
import ResolutionSelector from './components/ResolutionSelector';
import Controls from './components/Controls';
import VideoExporter from './components/VideoExporter';
import './styles/App.css';

function App() {
  const [audioFile, setAudioFile] = useState(null);
  const [selectedTemplate, setSelectedTemplate] = useState('waveform');
  const [selectedResolution, setSelectedResolution] = useState('1080p');
  const [isPlaying, setIsPlaying] = useState(false);
  const visualizerRef = useRef(null);
  const audioRef = useRef(null);

  const handleAudioUpload = (file) => {
    setAudioFile(file);
    setIsPlaying(false);
  };

  const handleTemplateChange = (template) => {
    setSelectedTemplate(template);
  };

  const handleResolutionChange = (resolution) => {
    setSelectedResolution(resolution);
    // Update visualizer resolution if ref is available
    if (visualizerRef.current && visualizerRef.current.changeResolution) {
      visualizerRef.current.changeResolution(resolution);
    }
  };

  const handlePlayPause = useCallback(() => {
    if (!audioRef.current) return;

    if (isPlaying) {
      audioRef.current.pause();
    } else {
      audioRef.current.play().catch(error => {
        console.error('Error playing audio:', error);
        alert('Failed to play audio. Please try again.');
      });
    }
    setIsPlaying(!isPlaying);
  }, [isPlaying]);

  const handleAudioElementCreated = useCallback((audioElement) => {
    audioRef.current = audioElement;
    if (audioElement) {
      // Add event listeners for audio state changes
      audioElement.addEventListener('ended', () => setIsPlaying(false));
      audioElement.addEventListener('pause', () => setIsPlaying(false));
      audioElement.addEventListener('play', () => setIsPlaying(true));
    }
  }, []);

  return (
    <div className="app">
      <header>
        <h1>Music Visualizer</h1>
        <div style={{ display: 'flex', gap: '1rem' }}>
          <TemplateSelector 
            selectedTemplate={selectedTemplate} 
            onTemplateChange={handleTemplateChange} 
          />
          <ResolutionSelector
            currentResolution={selectedResolution}
            onResolutionChange={handleResolutionChange}
          />
        </div>
      </header>
      <main>
        <div className="visualizer-section">
          <Visualizer
            ref={visualizerRef}
            audioFile={audioFile}
            template={selectedTemplate}
            isPlaying={isPlaying}
            onAudioElementCreated={handleAudioElementCreated}
          />
        </div>
        <div className="controls-section">
          <div className="controls-container">
            <div className="action-buttons">
              <AudioUploader onUpload={handleAudioUpload} />
              <VideoExporter 
                visualizerRef={visualizerRef} 
                audioFile={audioFile} 
                isPlaying={isPlaying}
                resolution={selectedResolution}
              />
            </div>
            <Controls 
              isPlaying={isPlaying} 
              onPlayPause={handlePlayPause} 
              audioElement={audioRef.current}
            />
          </div>
        </div>
      </main>
    </div>
  );
}

export default App;
