import React, { useState, useEffect } from 'react';
import './VisualizerControls.css';

const VisualizerControls = ({ template, onParametersChange, initialParameters }) => {
  // Define default parameters for each visualization type
  const defaultParameters = {
    // 2D Visualizations
    waveform: {
      color: '#00aaff',
      lineWidth: 3,
      reactivity: 50,
      smoothing: 50
    },
    bars: {
      color: '#ff5500',
      barWidth: 50,
      spacing: 30,
      reactivity: 50
    },
    circles: {
      color: '#ff00ff',
      radius: 50,
      count: 50,
      reactivity: 50
    },
    kaleidoscope: {
      color: '#ffaa00',
      segments: 8,
      speed: 50,
      reactivity: 50
    },
    spiral: {
      color: '#00ff99',
      arms: 5,
      speed: 50,
      reactivity: 50
    },
    ripple: {
      color: '#00ffff',
      speed: 50,
      density: 50,
      reactivity: 50
    },
    terrain: {
      color: '#33cc33',
      detail: 50,
      speed: 50,
      reactivity: 50
    },
    starburst: {
      color: '#ff9900',
      count: 50,
      size: 50,
      reactivity: 50
    },
    fractal: {
      color: '#66ff66',
      complexity: 50,
      variation: 50,
      reactivity: 50
    },
    liquid: {
      color: '#0099ff',
      complexity: 50,
      speed: 50,
      reactivity: 50
    },
    mesh: {
      color: '#ff00cc',
      density: 50,
      perspective: 50,
      reactivity: 50
    },
    clock: {
      color: '#ffcc00',
      detail: 50,
      speed: 50,
      reactivity: 50
    },
    particles: {
      color: '#ffffff',
      count: 50,
      size: 50,
      speed: 50,
      reactivity: 50
    },

    // 3D Visualizations
    particles3d: {
      color: '#ffffff',
      count: 50,
      size: 50,
      speed: 50,
      reactivity: 50
    },
    waveform3d: {
      color: '#00aaff',
      lineWidth: 3,
      height: 50,
      speed: 50,
      reactivity: 50
    },
    spiral3d: {
      color: '#00ff99',
      arms: 5,
      radius: 50,
      speed: 50,
      reactivity: 50
    },
    kaleidoscope3d: {
      color: '#ffaa00',
      complexity: 50,
      speed: 50,
      reactivity: 50
    }
  };

  // Initialize parameters state based on initialParameters or template defaults
  const [parameters, setParameters] = useState(
    initialParameters || defaultParameters[template] || defaultParameters.waveform
  );

  // Update parameters when template changes, but preserve user settings if available
  useEffect(() => {
    console.log("Template changed to:", template);
    // Only reset to defaults if the template changes
    if (!initialParameters || Object.keys(initialParameters).length === 0) {
      const newParams = defaultParameters[template] || defaultParameters.waveform;
      setParameters(newParams);
      onParametersChange(newParams);
    }
  }, [template, onParametersChange]);

  // Update local state when initialParameters change from parent
  useEffect(() => {
    if (initialParameters && Object.keys(initialParameters).length > 0) {
      console.log("Received updated parameters from parent:", initialParameters);
      setParameters(initialParameters);
    }
  }, [initialParameters]);

  // Handle parameter change
  const handleParameterChange = (param, value) => {
    console.log(`Changing parameter ${param} to ${value}`);
    const newParameters = { ...parameters, [param]: value };
    setParameters(newParameters);
    onParametersChange(newParameters);
  };

  // Get controls based on template
  const getControls = () => {
    // Common controls for all visualizations
    const commonControls = (
      <>
        <div className="parameter-group">
          <label>Reactivity</label>
          <div className="slider-container">
            <input
              type="range"
              min="0"
              max="100"
              value={parameters.reactivity}
              onChange={(e) => handleParameterChange('reactivity', parseInt(e.target.value))}
            />
            <span className="slider-value">{parameters.reactivity}%</span>
          </div>
        </div>

        <div className="parameter-group">
          <label>Color</label>
          <input
            type="color"
            value={parameters.color}
            onChange={(e) => handleParameterChange('color', e.target.value)}
            className="color-picker"
          />
        </div>
      </>
    );

    // Template-specific controls
    switch (template) {
      case 'waveform':
      case 'waveform3d':
        return (
          <>
            {commonControls}
            <div className="parameter-group">
              <label>Line Width</label>
              <div className="slider-container">
                <input
                  type="range"
                  min="1"
                  max="10"
                  value={parameters.lineWidth}
                  onChange={(e) => handleParameterChange('lineWidth', parseInt(e.target.value))}
                />
                <span className="slider-value">{parameters.lineWidth}px</span>
              </div>
            </div>
            <div className="parameter-group">
              <label>Smoothing</label>
              <div className="slider-container">
                <input
                  type="range"
                  min="0"
                  max="100"
                  value={parameters.smoothing}
                  onChange={(e) => handleParameterChange('smoothing', parseInt(e.target.value))}
                />
                <span className="slider-value">{parameters.smoothing}%</span>
              </div>
            </div>
          </>
        );

      case 'bars':
        return (
