import React, { useEffect, useRef, useState } from 'react';
import './Controls.css';

const Controls = ({ isPlaying, onPlayPause, audioElement }) => {
  const [currentTime, setCurrentTime] = useState(0);
  const [duration, setDuration] = useState(0);
  const progressRef = useRef(null);
  const isDraggingRef = useRef(false);

  useEffect(() => {
    if (!audioElement) return;

    const handleTimeUpdate = () => {
      if (!isDraggingRef.current) {
        setCurrentTime(audioElement.currentTime);
      }
    };

    const handleLoadedMetadata = () => {
      setDuration(audioElement.duration);
    };

    audioElement.addEventListener('timeupdate', handleTimeUpdate);
    audioElement.addEventListener('loadedmetadata', handleLoadedMetadata);

    return () => {
      audioElement.removeEventListener('timeupdate', handleTimeUpdate);
      audioElement.removeEventListener('loadedmetadata', handleLoadedMetadata);
    };
  }, [audioElement]);

  const formatTime = (time) => {
    const minutes = Math.floor(time / 60);
    const seconds = Math.floor(time % 60);
    return `${minutes}:${seconds.toString().padStart(2, '0')}`;
  };

  const handleProgressClick = (e) => {
    if (!audioElement || !progressRef.current) return;

    const rect = progressRef.current.getBoundingClientRect();
    const pos = (e.clientX - rect.left) / rect.width;
    audioElement.currentTime = pos * duration;
  };

  const handleProgressMouseDown = () => {
    isDraggingRef.current = true;
  };

  const handleProgressMouseUp = () => {
    isDraggingRef.current = false;
  };

  const handleProgressMouseMove = (e) => {
    if (!isDraggingRef.current || !audioElement || !progressRef.current) return;

    const rect = progressRef.current.getBoundingClientRect();
    const pos = (e.clientX - rect.left) / rect.width;
    const newTime = Math.max(0, Math.min(pos * duration, duration));
    
    setCurrentTime(newTime);
    audioElement.currentTime = newTime;
  };

  useEffect(() => {
    document.addEventListener('mouseup', handleProgressMouseUp);
    document.addEventListener('mousemove', handleProgressMouseMove);

    return () => {
      document.removeEventListener('mouseup', handleProgressMouseUp);
      document.removeEventListener('mousemove', handleProgressMouseMove);
    };
  }, [duration]);

  return (
    <div className="controls">
      <div className="playback-info">
        <span className="time">{formatTime(currentTime)}</span>
        <div 
          className="progress-bar"
          ref={progressRef}
          onClick={handleProgressClick}
          onMouseDown={handleProgressMouseDown}
        >
          <div 
            className="progress-bar-fill"
            style={{ width: `${(currentTime / duration) * 100}%` }}
          />
          <div 
            className="progress-handle"
            style={{ left: `${(currentTime / duration) * 100}%` }}
          />
        </div>
        <span className="time">{formatTime(duration)}</span>
      </div>
      <button
        className={`play-pause-button ${isPlaying ? 'playing' : ''}`}
        onClick={onPlayPause}
      >
        {isPlaying ? (
          <svg viewBox="0 0 24 24" className="control-icon">
            <rect x="6" y="4" width="4" height="16"/>
            <rect x="14" y="4" width="4" height="16"/>
          </svg>
        ) : (
          <svg viewBox="0 0 24 24" className="control-icon">
            <path d="M8 5v14l11-7z"/>
          </svg>
        )}
      </button>
    </div>
  );
};

export default Controls;
