import React from 'react';
import './Controls.css';

const Controls = ({ isPlaying, onPlayPause }) => {
  return (
    <div className="controls">
      <button
        className={`play-pause-button ${isPlaying ? 'playing' : ''}`}
        onClick={onPlayPause}
      >
        {isPlaying ? 'Pause' : 'Play'}
      </button>
    </div>
  );
};

export default Controls;
