import React, { useRef, useState, useEffect } from 'react';
import './VideoExporter.css';

const VideoExporter = ({ visualizerRef, audioFile, isPlaying }) => {
  const [isExporting, setIsExporting] = useState(false);
  const [progress, setProgress] = useState(0);
  const mediaRecorderRef = useRef(null);
  const chunksRef = useRef([]);
  const startTimeRef = useRef(null);
  const audioDurationRef = useRef(null);
  const progressIntervalRef = useRef(null);

  // Get audio duration when audio file changes
  useEffect(() => {
    if (audioFile) {
      // Use the same URL format as in Visualizer component
      const timestamp = new Date().getTime();
      const audioUrl = `http://localhost:5001${audioFile.path}?t=${timestamp}`;
      const audio = new Audio(audioUrl);

      audio.addEventListener('loadedmetadata', () => {
        audioDurationRef.current = audio.duration;
      });

      // Add error handling
      audio.addEventListener('error', (e) => {
        console.error('Error loading audio for duration calculation:', e);
      });
    }
    return () => {
      if (progressIntervalRef.current) {
        clearInterval(progressIntervalRef.current);
      }
    };
  }, [audioFile]);

  const exportVideo = async () => {
    if (!visualizerRef?.current || !audioFile) {
      alert("Please upload an audio file first");
      return;
    }

    try {
      setIsExporting(true);
      setProgress(0);
      startTimeRef.current = Date.now();

      // Get the canvas element from the visualizer using the exposed method
      const canvas = visualizerRef.current.getCanvas();
      if (!canvas) {
        throw new Error("Canvas element not found");
      }

      // Create a stream from the canvas
      let stream;
      try {
        // Try standard method first
        stream = canvas.captureStream(30);
      } catch (e) {
        // Fallback for Firefox
        stream = canvas.mozCaptureStream ? canvas.mozCaptureStream(30) : null;
      }

      if (!stream) {
        throw new Error("Your browser doesn't support canvas streaming");
      }

      // Create audio context and connect to stream
      const audioContext = new (window.AudioContext || window.webkitAudioContext)();

      // Use the same URL format as in Visualizer component
      const timestamp = new Date().getTime();
      const audioUrl = `http://localhost:5001${audioFile.path}?t=${timestamp}`;
      const audioElement = new Audio(audioUrl);

      // Add error handling for audio element
      audioElement.addEventListener('error', (e) => {
        console.error('Audio element error:', e);
        throw new Error(`Failed to load audio: ${e.message}`);
      });

      // Wait for audio to be loadable before proceeding
      await new Promise((resolve, reject) => {
        audioElement.addEventListener('canplaythrough', resolve, { once: true });
        audioElement.addEventListener('error', (e) => reject(new Error(`Audio load error: ${e.message}`)), { once: true });
        audioElement.load();
      });

      const audioSource = audioContext.createMediaElementSource(audioElement);
      const audioDestination = audioContext.createMediaStreamDestination();
      audioSource.connect(audioDestination);
      audioSource.connect(audioContext.destination); // Also connect to speakers

      // Combine video and audio streams
      const combinedStream = new MediaStream([
        ...stream.getVideoTracks(),
        ...audioDestination.stream.getAudioTracks()
      ]);

      // Create media recorder with appropriate MIME type
      let mimeType = 'video/webm;codecs=vp9,opus';
      if (!MediaRecorder.isTypeSupported(mimeType)) {
        // Fallback to more compatible options
        if (MediaRecorder.isTypeSupported('video/webm;codecs=vp8,opus')) {
          mimeType = 'video/webm;codecs=vp8,opus';
        } else if (MediaRecorder.isTypeSupported('video/webm')) {
          mimeType = 'video/webm';
        }
      }

      const mediaRecorder = new MediaRecorder(combinedStream, { mimeType });
      mediaRecorderRef.current = mediaRecorder;
      chunksRef.current = [];

      // Handle data available
      mediaRecorder.ondataavailable = (event) => {
        if (event.data.size > 0) {
          chunksRef.current.push(event.data);
        }
      };

      // Handle recording stop
      mediaRecorder.onstop = () => {
        const blob = new Blob(chunksRef.current, { type: mimeType });
        const url = URL.createObjectURL(blob);

        // Create filename based on audio file name with fallback
        let fileName = "visualization.webm";
        if (audioFile && audioFile.name) {
          fileName = audioFile.name.replace(/\.[^/.]+$/, "") + "_visualization.webm";
        } else if (audioFile && audioFile.path) {
          // Extract filename from path as fallback
          const pathParts = audioFile.path.split('/');
          const pathFileName = pathParts[pathParts.length - 1];
          fileName = pathFileName.replace(/\.[^/.]+$/, "") + "_visualization.webm";
        }

        // Download the file
        const a = document.createElement('a');
        a.href = url;
        a.download = fileName;
        a.click();

        // Clean up
        URL.revokeObjectURL(url);
        setIsExporting(false);
        setProgress(100);

        // Clear progress interval
        if (progressIntervalRef.current) {
          clearInterval(progressIntervalRef.current);
          progressIntervalRef.current = null;
        }
      };

      // Start recording
      mediaRecorder.start(1000); // Capture in 1-second chunks

      // Start audio playback
      try {
        await audioElement.play();
      } catch (playError) {
        console.error('Error playing audio:', playError);
        throw new Error(`Failed to play audio: ${playError.message}`);
      }

      // Update progress based on audio time
      progressIntervalRef.current = setInterval(() => {
        if (audioDurationRef.current) {
          const elapsed = (Date.now() - startTimeRef.current) / 1000;
          const percentage = Math.min(Math.floor((elapsed / audioDurationRef.current) * 100), 99);
          setProgress(percentage);
        }
      }, 500);

      // Stop recording when audio ends
      audioElement.onended = () => {
        if (mediaRecorderRef.current && mediaRecorderRef.current.state === 'recording') {
          mediaRecorderRef.current.stop();
          audioElement.onended = null;
        }
      };

    } catch (error) {
      console.error("Error exporting video:", error);
      alert(`Error exporting video: ${error.message}`);
      setIsExporting(false);
    }
  };

  return (
    <div className="video-exporter">
      {isExporting ? (
        <div className="export-progress">
          <div className="progress-bar">
            <div className="progress-fill" style={{ width: `${progress}%` }}></div>
          </div>
          <div className="progress-text">{progress}% Exporting...</div>
        </div>
      ) : (
        <button
          className="export-button"
          onClick={exportVideo}
          disabled={!visualizerRef || !audioFile || isPlaying}
        >
          Export Video
        </button>
      )}
    </div>
  );
};

export default VideoExporter;
