import React, { useEffect, useRef, forwardRef, useState } from 'react';
import ThreeVisualizer from './ThreeVisualizer';
import './Visualizer.css';

const RESOLUTIONS = {
  '4K': { width: 3840, height: 2160, className: 'aspect-ratio-4k' },
  '1080p': { width: 1920, height: 1080, className: 'aspect-ratio-1080p' },
  '720p': { width: 1280, height: 720, className: 'aspect-ratio-720p' },
  'Square': { width: 720, height: 720, className: 'aspect-ratio-square' },
  'Vertical': { width: 1080, height: 1920, className: 'aspect-ratio-vertical' }
};

const Visualizer = forwardRef(({ audioFile, template, isPlaying, onAudioElementCreated }, ref) => {
  const canvasRef = useRef(null);
  const containerRef = useRef(null);
  const audioContextRef = useRef(null);
  const analyserRef = useRef(null);
  const animationFrameRef = useRef(null);
  const sourceRef = useRef(null);
  const audioElementRef = useRef(null);
  const particlesRef = useRef([]);
  const [resolution, setResolution] = useState('1080p'); // Default to 1080p
  const [audioData, setAudioData] = useState(null);
  const [use3D, setUse3D] = useState(false);

  // Check if template is a 3D visualization
  useEffect(() => {
    // Define which templates should use 3D rendering
    const threeDTemplates = ['particles3d', 'waveform3d', 'spiral3d', 'kaleidoscope3d'];
    setUse3D(threeDTemplates.includes(template));
  }, [template]);

  // Audio initialization effect
  useEffect(() => {
    if (!audioFile) return;

    let isInitialized = false;

    const initAudio = async () => {
      try {
        // Initialize audio context only on first load or if it doesn't exist
        if (!audioContextRef.current) {
          // Create context on user interaction
          const context = new (window.AudioContext || window.webkitAudioContext)();
          audioContextRef.current = context;
          
          // Create analyzer
          const analyser = context.createAnalyser();
          analyser.fftSize = 2048;
          analyserRef.current = analyser;
        }

        // Create and set up audio element
        const audioElement = new Audio();
        audioElement.crossOrigin = "anonymous";
        audioElement.preload = "auto";

        // Set the audio source
        const timestamp = new Date().getTime();
        audioElement.src = `http://localhost:5001${audioFile.path}?t=${timestamp}`;

        // Wait for audio to be loaded enough to play
        await new Promise((resolve, reject) => {
          const loadHandler = () => {
            console.log('Audio loaded, duration:', audioElement.duration);
            resolve();
          };

          const errorHandler = (error) => {
            console.error('Audio load error:', error);
            reject(error);
          };

          audioElement.addEventListener('canplaythrough', loadHandler, { once: true });
          audioElement.addEventListener('error', errorHandler, { once: true });
          audioElement.load();
        });

        // Only create new media source if not already connected
        if (!isInitialized) {
          // Ensure old source is disconnected
          if (sourceRef.current) {
            sourceRef.current.disconnect();
          }

          // Create and connect new media source
          const mediaSource = audioContextRef.current.createMediaElementSource(audioElement);
          sourceRef.current = mediaSource;
          mediaSource.connect(analyserRef.current);
          analyserRef.current.connect(audioContextRef.current.destination);
          isInitialized = true;
        }

        // Set up event listeners
        const onSeeking = () => {
          console.log('Seeking:', audioElement.currentTime);
          if (isPlaying) startVisualization();
        };

        const onTimeUpdate = () => {
          console.log('Time update:', audioElement.currentTime);
          if (isPlaying) startVisualization();
        };

        audioElement.addEventListener('seeking', onSeeking);
        audioElement.addEventListener('seeked', onSeeking);
        audioElement.addEventListener('timeupdate', onTimeUpdate);

        // Store reference and notify parent
        audioElementRef.current = audioElement;
        onAudioElementCreated(audioElement);

        // Resume audio context if suspended
        if (audioContextRef.current.state === 'suspended') {
          await audioContextRef.current.resume();
        }

        return () => {
          console.log('Cleaning up audio element');
          audioElement.removeEventListener('seeking', onSeeking);
          audioElement.removeEventListener('seeked', onSeeking);
          audioElement.removeEventListener('timeupdate', onTimeUpdate);
          audioElement.pause();
          audioElement.src = '';
          onAudioElementCreated(null);
        };
      } catch (error) {
        console.error('Audio initialization error:', error);
        alert('Error loading audio. Please try again.');
      }
    };

    initAudio();
  }, [audioFile]); // Remove isPlaying from dependencies

  // Separate playback control effect
  useEffect(() => {
    const audioElement = audioElementRef.current;
    if (!audioElement) return;

    const playAudio = async () => {
      try {
        // Ensure audio context is running
        if (audioContextRef.current?.state === 'suspended') {
          await audioContextRef.current.resume();
        }
        
        console.log('Playing audio...');
        await audioElement.play();
        startVisualization();
      } catch (error) {
        console.error('Playback error:', error);
        alert('Error playing audio. Please try again.');
      }
    };

    if (isPlaying) {
      playAudio();
    } else {
      console.log('Pausing audio...');
      audioElement.pause();
      cancelAnimationFrame(animationFrameRef.current);
    }
  }, [isPlaying]);

  // Add effect to handle template changes
  useEffect(() => {
    if (!canvasRef.current) return;
    
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    
    // Clear the entire canvas
    ctx.fillStyle = 'rgb(0, 0, 0)';
    ctx.fillRect(0, 0, canvas.width, canvas.height);

    // Restart visualization if playing
    if (isPlaying) {
      startVisualization();
    }
  }, [template]);

  // Update canvas size based on selected resolution
  useEffect(() => {
    if (!canvasRef.current) return;
    
    const canvas = canvasRef.current;
    const { width, height } = RESOLUTIONS[resolution];
    
    // Set canvas dimensions to match the selected resolution
    canvas.width = width;
    canvas.height = height;
    
    // Clear canvas with new dimensions
    const ctx = canvas.getContext('2d');
    ctx.fillStyle = 'rgb(0, 0, 0)';
    ctx.fillRect(0, 0, canvas.width, canvas.height);
    
    // Adjust container styles based on aspect ratio
    if (containerRef.current) {
      if (resolution === 'Vertical') {
        containerRef.current.style.width = 'auto';
        containerRef.current.style.height = '80vh';
      } else if (resolution === 'Square') {
        containerRef.current.style.width = '720px';
        containerRef.current.style.height = '720px';
        containerRef.current.style.maxWidth = '720px';
        containerRef.current.style.maxHeight = '720px';
      } else {
        containerRef.current.style.width = '100%';
        containerRef.current.style.height = 'auto';
        containerRef.current.style.maxWidth = `${width}px`;
        containerRef.current.style.maxHeight = `${height}px`;
      }
    }
    
    // Restart visualization if playing
    if (isPlaying) {
      startVisualization();
    }
    
    // Initialize particles for the new canvas size
    if (template === 'particles') {
      initParticles(ctx);
    }
  }, [resolution, isPlaying, template]);

  // Function to change resolution
  const changeResolution = (newResolution) => {
    if (RESOLUTIONS[newResolution]) {
      setResolution(newResolution);
    }
  };

  // Expose changeResolution to parent via ref
  React.useImperativeHandle(ref, () => ({
    changeResolution,
    getCurrentResolution: () => resolution
  }));

  const initParticles = (ctx) => {
    particlesRef.current = [];
    const numParticles = 100;
    
    for (let i = 0; i < numParticles; i++) {
      particlesRef.current.push({
        x: Math.random() * ctx.canvas.width,
        y: Math.random() * ctx.canvas.height,
        size: Math.random() * 4 + 2,
        speedX: Math.random() * 4 - 2,
        speedY: Math.random() * 4 - 2,
        hue: Math.random() * 360
      });
    }
  };

  const updateAndDrawParticles = (ctx, dataArray, bufferLength) => {
    const avgFreq = dataArray.reduce((a, b) => a + b, 0) / bufferLength;
    const intensity = avgFreq / 256;
    
    particlesRef.current.forEach((particle, i) => {
      // Update position
      particle.x += particle.speedX * (1 + intensity);
      particle.y += particle.speedY * (1 + intensity);
      
      // Wrap around screen
      if (particle.x < 0) particle.x = ctx.canvas.width;
      if (particle.x > ctx.canvas.width) particle.x = 0;
      if (particle.y < 0) particle.y = ctx.canvas.height;
      if (particle.y > ctx.canvas.height) particle.y = 0;
      
      // Update color based on frequency
      const freqIndex = Math.floor((i / particlesRef.current.length) * bufferLength);
      const freq = dataArray[freqIndex];
      const hue = (particle.hue + freq / 2) % 360;
      
      // Draw particle
      ctx.beginPath();
      ctx.arc(
        particle.x,
        particle.y,
        particle.size * (1 + intensity * 0.5),
        0,
        Math.PI * 2
      );
      ctx.fillStyle = `hsla(${hue}, 100%, 50%, ${0.7 + intensity * 0.3})`;
      ctx.fill();
      
      // Draw connections
      particlesRef.current.forEach((otherParticle, j) => {
        if (i === j) return;
        
        const dx = particle.x - otherParticle.x;
        const dy = particle.y - otherParticle.y;
        const distance = Math.sqrt(dx * dx + dy * dy);
        
        if (distance < 100) {
          ctx.beginPath();
          ctx.moveTo(particle.x, particle.y);
          ctx.lineTo(otherParticle.x, otherParticle.y);
          ctx.strokeStyle = `hsla(${hue}, 100%, 50%, ${(1 - distance / 100) * 0.2})`;
          ctx.lineWidth = 1;
          ctx.stroke();
        }
      });
    });
  };

  // Start visualization
  const startVisualization = () => {
    if (!analyserRef.current || !canvasRef.current) return;
    
    const analyser = analyserRef.current;
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    
    // Create data arrays for audio analysis
    const bufferLength = analyser.frequencyBinCount;
    const dataArray = new Uint8Array(bufferLength);
    const timeArray = new Uint8Array(bufferLength);
    
    // Get initial data
    analyser.getByteFrequencyData(dataArray);
    analyser.getByteTimeDomainData(timeArray);
    
    // Update audio data for 3D visualizer
    setAudioData({ 
      dataArray: Array.from(dataArray), 
      timeArray: Array.from(timeArray), 
      bufferLength 
    });
    
    // Cancel any existing animation frame
    if (animationFrameRef.current) {
      cancelAnimationFrame(animationFrameRef.current);
    }
    
    // Skip 2D visualization if using 3D
    if (use3D) {
      // For 3D, we need to continuously update the audio data
      const update3DAudio = () => {
        animationFrameRef.current = requestAnimationFrame(update3DAudio);
        
        // Get frequency and time domain data
        analyser.getByteFrequencyData(dataArray);
        analyser.getByteTimeDomainData(timeArray);
        
        // Update audio data for 3D visualizer
        setAudioData({ 
          dataArray: Array.from(dataArray), 
          timeArray: Array.from(timeArray), 
          bufferLength 
        });
      };
      
      update3DAudio();
      return;
    }
    
    // Animation function for 2D visualizations
    const draw = () => {
      animationFrameRef.current = requestAnimationFrame(draw);
      
      // Get frequency and time domain data
      analyser.getByteFrequencyData(dataArray);
      analyser.getByteTimeDomainData(timeArray);
      
      // Clear canvas
      ctx.fillStyle = 'rgb(0, 0, 0)';
      ctx.fillRect(0, 0, canvas.width, canvas.height);
      
      // Draw visualization based on template
      switch (template) {
        case 'waveform':
          drawWaveform(ctx, dataArray, bufferLength);
          break;
        case 'bars':
          drawBars(ctx, dataArray, bufferLength);
          break;
        case 'circles':
          drawCircles(ctx, dataArray, bufferLength);
          break;
        case 'kaleidoscope':
          drawKaleidoscope(ctx, dataArray, timeArray, bufferLength);
          break;
        case 'spiral':
          drawSpiral(ctx, dataArray, timeArray, bufferLength);
          break;
        case 'particles':
          updateAndDrawParticles(ctx, dataArray, bufferLength);
          break;
        default:
          drawWaveform(ctx, dataArray, bufferLength);
      }
    };
    
    draw();
  };

  const drawWaveform = (ctx, dataArray, bufferLength) => {
    ctx.lineWidth = 2;
    ctx.strokeStyle = 'rgb(0, 255, 0)';
    ctx.beginPath();

    const sliceWidth = ctx.canvas.width / bufferLength;
    let x = 0;

    for (let i = 0; i < bufferLength; i++) {
      const v = dataArray[i] / 128.0;
      const y = v * ctx.canvas.height / 2;

      if (i === 0) {
        ctx.moveTo(x, y);
      } else {
        ctx.lineTo(x, y);
      }

      x += sliceWidth;
    }

    ctx.lineTo(ctx.canvas.width, ctx.canvas.height / 2);
    ctx.stroke();
  };

  const drawBars = (ctx, dataArray, bufferLength) => {
    const barWidth = (ctx.canvas.width / bufferLength) * 2.5;
    let barHeight;
    let x = 0;

    for (let i = 0; i < bufferLength; i++) {
      barHeight = dataArray[i] / 2;

      ctx.fillStyle = `rgb(${barHeight + 100}, 50, 50)`;
      ctx.fillRect(x, ctx.canvas.height - barHeight, barWidth, barHeight);

      x += barWidth + 1;
    }
  };

  const drawCircles = (ctx, dataArray, bufferLength) => {
    const centerX = ctx.canvas.width / 2;
    const centerY = ctx.canvas.height / 2;
    const maxRadius = Math.min(centerX, centerY) * 0.8;

    for (let i = 0; i < bufferLength; i++) {
      const radius = (dataArray[i] / 255) * maxRadius;
      const angle = (i / bufferLength) * Math.PI * 2;

      const x = centerX + Math.cos(angle) * radius;
      const y = centerY + Math.sin(angle) * radius;

      ctx.beginPath();
      ctx.arc(x, y, 2, 0, Math.PI * 2);
      ctx.fillStyle = `hsl(${(i / bufferLength) * 360}, 100%, 50%)`;
      ctx.fill();
    }
  };

  const drawKaleidoscope = (ctx, dataArray, timeArray, bufferLength) => {
    const centerX = ctx.canvas.width / 2;
    const centerY = ctx.canvas.height / 2;
    const segments = 8;
    const angleStep = (Math.PI * 2) / segments;
    
    // Calculate average frequency for color
    const avgFreq = dataArray.reduce((a, b) => a + b, 0) / bufferLength;
    const hue = (avgFreq + performance.now() / 50) % 360;
    
    ctx.save();
    ctx.translate(centerX, centerY);

    for (let s = 0; s < segments; s++) {
      ctx.rotate(angleStep);
      
      ctx.beginPath();
      ctx.moveTo(0, 0);
      
      for (let i = 0; i < bufferLength; i += 4) {
        const freq = dataArray[i];
        const time = timeArray[i];
        const x = (i / bufferLength) * 200;
        const y = (freq / 256) * 200;
        
        const wave = Math.sin(time / 128 * Math.PI + x / 50) * 20;
        
        ctx.lineTo(x, y + wave);
      }
      
      ctx.strokeStyle = `hsl(${hue}, 100%, 50%)`;
      ctx.lineWidth = 2;
      ctx.stroke();
      
      // Mirror effect
      ctx.scale(-1, 1);
    }
    
    ctx.restore();
  };

  const drawSpiral = (ctx, dataArray, timeArray, bufferLength) => {
    const centerX = ctx.canvas.width / 2;
    const centerY = ctx.canvas.height / 2;
    const maxRadius = Math.min(centerX, centerY) * 0.9;
    
    // Calculate average frequency for effects
    const avgFreq = dataArray.reduce((a, b) => a + b, 0) / bufferLength;
    const time = performance.now() / 1000;
    
    ctx.save();
    ctx.translate(centerX, centerY);
    
    for (let i = 0; i < bufferLength; i += 2) {
      const freq = dataArray[i];
      const amplitude = freq / 256;
      
      // Create spiral effect
      const angle = (i / bufferLength) * Math.PI * 20 + time;
      const radius = (i / bufferLength) * maxRadius * (1 + amplitude * 0.2);
      
      const x = Math.cos(angle) * radius;
      const y = Math.sin(angle) * radius;
      
      const hue = (angle * 30 + time * 50) % 360;
      const size = 2 + amplitude * 5;
      
      ctx.beginPath();
      ctx.arc(x, y, size, 0, Math.PI * 2);
      ctx.fillStyle = `hsla(${hue}, 100%, 50%, ${0.5 + amplitude * 0.5})`;
      ctx.fill();
      
      // Add connecting lines for more psychedelic effect
      if (i > 0) {
        const prevAngle = ((i - 2) / bufferLength) * Math.PI * 20 + time;
        const prevRadius = ((i - 2) / bufferLength) * maxRadius;
        const prevX = Math.cos(prevAngle) * prevRadius;
        const prevY = Math.sin(prevAngle) * prevRadius;
        
        ctx.beginPath();
        ctx.moveTo(prevX, prevY);
        ctx.lineTo(x, y);
        ctx.strokeStyle = `hsla(${hue}, 100%, 50%, 0.3)`;
        ctx.lineWidth = 1;
        ctx.stroke();
      }
    }
    
    ctx.restore();
  };

  return (
    <div className="visualizer-container">
      <div 
        ref={containerRef}
        className={`visualizer-aspect-container ${RESOLUTIONS[resolution].className}`}
      >
        {use3D ? (
          <ThreeVisualizer 
            audioData={audioData}
            template={template.replace('3d', '')} // Remove '3d' suffix for the ThreeVisualizer
            isPlaying={isPlaying}
          />
        ) : (
          <canvas ref={canvasRef} className="visualizer-canvas"></canvas>
        )}
        <div className="visualizer-overlay"></div>
        <div className="resolution-indicator">{resolution} ({RESOLUTIONS[resolution].width}Ã—{RESOLUTIONS[resolution].height})</div>
      </div>
    </div>
  );
});

export default Visualizer;
