import React, { useRef, useState, useEffect } from 'react';
import './VideoExporter.css';

// Quality presets for different export options
const QUALITY_PRESETS = {
  high: {
    videoBitrate: 8000000,
    audioBitrate: 320000,
    frameRate: 60,
    label: 'High Quality'
  },
  medium: {
    videoBitrate: 4000000,
    audioBitrate: 192000,
    frameRate: 30,
    label: 'Medium Quality'
  },
  low: {
    videoBitrate: 2000000,
    audioBitrate: 128000,
    frameRate: 30,
    label: 'Low Quality'
  }
};

// Export format options with MIME type checks
const EXPORT_FORMATS = {
  webm: {
    mimeType: 'video/webm;codecs=vp9,opus',
    extension: 'webm',
    label: 'WebM (VP9)',
    fallback: null
  },
  mp4: {
    mimeType: 'video/mp4;codecs=h264,aac',
    extension: 'mp4',
    label: 'MP4 (H.264)',
    fallback: 'webm'
  }
};

// Maximum number of retries for failed exports
const MAX_RETRIES = 3;

const VideoExporter = ({ visualizerRef, audioFile, isPlaying }) => {
  const [isExporting, setIsExporting] = useState(false);
  const [progress, setProgress] = useState(0);
  const [debug, setDebug] = useState('');
  const [selectedQuality, setSelectedQuality] = useState('high');
  const [selectedFormat, setSelectedFormat] = useState('webm');
  const [retryCount, setRetryCount] = useState(0);
  const [supportedFormats, setSupportedFormats] = useState([]);

  const mediaRecorderRef = useRef(null);
  const chunksRef = useRef([]);
  const startTimeRef = useRef(null);
  const audioDurationRef = useRef(null);
  const progressIntervalRef = useRef(null);
  const dataRequestIntervalRef = useRef(null);
  const audioContextRef = useRef(null);
  const audioSourceRef = useRef(null);
  const audioElementRef = useRef(null);
  const originalAudioRef = useRef(null);
  const originalIsPlayingRef = useRef(false);

  // Check supported formats on component mount
  useEffect(() => {
    const checkSupportedFormats = () => {
      const supported = Object.entries(EXPORT_FORMATS)
        .filter(([_, format]) => MediaRecorder.isTypeSupported(format.mimeType))
        .map(([key]) => key);

      setSupportedFormats(supported);
      setDebug(`Supported formats: ${supported.join(', ')}`);

      // If current format is not supported, switch to a supported one
      if (!supported.includes(selectedFormat)) {
        const fallbackFormat = EXPORT_FORMATS[selectedFormat].fallback;
        if (fallbackFormat && supported.includes(fallbackFormat)) {
          setSelectedFormat(fallbackFormat);
          setDebug(`Switched to fallback format: ${fallbackFormat}`);
        } else if (supported.length > 0) {
          setSelectedFormat(supported[0]);
          setDebug(`Switched to first supported format: ${supported[0]}`);
        }
      }
    };

    checkSupportedFormats();
  }, []);

  // Clean up on unmount
  useEffect(() => {
    return () => {
      cleanupResources();
    };
  }, []);

  // Store the original audio element and isPlaying state
  useEffect(() => {
    const audioElements = document.querySelectorAll('audio');
    if (audioElements.length > 0) {
      originalAudioRef.current = audioElements[0];
      console.log('Found original audio element:', originalAudioRef.current);
    }
    originalIsPlayingRef.current = isPlaying;
  }, [isPlaying]);

  // Clean up resources function
  const cleanupResources = () => {
    if (originalIsPlayingRef.current && originalAudioRef.current) {
      try {
        originalAudioRef.current.play().catch(e => {
          console.error('Error resuming original audio:', e);
        });
      } catch (e) {
        console.error('Error resuming original audio:', e);
      }
    }

    if (progressIntervalRef.current) {
      clearInterval(progressIntervalRef.current);
      progressIntervalRef.current = null;
    }

    if (dataRequestIntervalRef.current) {
      clearInterval(dataRequestIntervalRef.current);
      dataRequestIntervalRef.current = null;
    }

    if (audioSourceRef.current) {
      try {
        audioSourceRef.current.disconnect();
      } catch (e) {
        console.error('Error disconnecting audio source:', e);
      }
      audioSourceRef.current = null;
    }

    if (audioElementRef.current) {
      try {
        audioElementRef.current.pause();
        audioElementRef.current.oncanplaythrough = null;
        audioElementRef.current.onerror = null;
        audioElementRef.current.onended = null;
        audioElementRef.current.removeAttribute('src');
        audioElementRef.current.load();
      } catch (e) {
        console.error('Error cleaning up audio element:', e);
      }
      audioElementRef.current = null;
    }

    if (audioContextRef.current && audioContextRef.current.state !== 'closed') {
      try {
        audioContextRef.current.close().catch(e => console.error('Error closing audio context:', e));
      } catch (e) {
        console.error('Error closing audio context:', e);
      }
      audioContextRef.current = null;
    }

    if (mediaRecorderRef.current && mediaRecorderRef.current.state === 'recording') {
      try {
        mediaRecorderRef.current.stop();
      } catch (e) {
        console.error('Error stopping media recorder:', e);
      }
      mediaRecorderRef.current = null;
    }

    chunksRef.current = [];
  };

  // Validate audio file
  const validateAudioFile = () => {
    if (!audioFile) {
      setDebug('Error: No audio file selected');
      return false;
    }

    if (!audioFile.path) {
      setDebug('Error: Audio file has no path');
      return false;
    }

    return true;
  };

  // Ensure visualizer is active
  const ensureVisualizerActive = () => {
    if (!visualizerRef?.current) {
      setDebug('Error: Visualizer reference not available');
      return false;
    }

    const canvas = visualizerRef.current.getCanvas();
    if (!canvas) {
      setDebug('Error: Canvas not found in visualizer');
      return false;
    }

    try {
      const ctx = canvas.getContext('2d');
      const imageData = ctx.getImageData(0, 0, canvas.width, canvas.height);
      ctx.putImageData(imageData, 0, 0);
      return true;
    } catch (e) {
      setDebug(`Error activating visualizer: ${e.message}`);
      return false;
    }
  };

  // Get canvas resolution
  const getCanvasResolution = (canvas) => {
    if (!canvas) return { width: 0, height: 0 };

    const width = canvas.width;
    const height = canvas.height;

    console.log(`Canvas resolution: ${width}x${height}`);
    setDebug(`Canvas resolution: ${width}x${height}`);

    return { width, height };
  };

  // Activate visualizer with original audio
  const activateVisualizerWithOriginalAudio = async () => {
    try {
      const playButtons = document.querySelectorAll('button');
      let playButton = null;

      for (const button of playButtons) {
        if (button.textContent.includes('Play') || 
            button.className.includes('play') || 
            button.title.includes('Play')) {
          playButton = button;
          break;
        }
      }

      originalIsPlayingRef.current = isPlaying;

      if (playButton && !isPlaying) {
        setDebug('Clicking play button to activate visualizer');
        playButton.click();

        await new Promise(resolve => setTimeout(resolve, 500));

        if (originalAudioRef.current) {
          originalAudioRef.current.pause();
          setDebug('Paused original audio to avoid duplication');
        } else if (playButton) {
          playButton.click();
          setDebug('Clicked play button again to pause audio');
        }

        return true;
      } else {
        setDebug('Audio is already playing or play button not found');
        return isPlaying;
      }
    } catch (e) {
      setDebug(`Error activating visualizer: ${e.message}`);
      return false;
    }
  };

  // Retry mechanism for failed exports
  const retryExport = async (exportFunction, maxRetries = MAX_RETRIES) => {
    for (let i = 0; i < maxRetries; i++) {
      try {
        setRetryCount(i + 1);
        setDebug(`Attempt ${i + 1} of ${maxRetries}`);
        await exportFunction();
        return;
      } catch (error) {
        if (i === maxRetries - 1) throw error;
        setDebug(`Export failed, retrying in ${i + 1} seconds...`);
        await new Promise(resolve => setTimeout(resolve, 1000 * (i + 1)));
      }
    }
  };

  const exportVideo = async () => {
    if (!visualizerRef?.current || !audioFile) {
      alert("Please upload an audio file first");
      return;
    }

    if (isExporting) {
      console.log('Already exporting, ignoring request');
      return;
    }

    try {
      cleanupResources();
      setIsExporting(true);
      setProgress(0);
      setDebug('Starting export process...');
      startTimeRef.current = Date.now();

      // 0. Activate visualizer by clicking play button
      const playButtons = document.querySelectorAll('button');
      let playButton = null;

      for (const button of playButtons) {
        if (button.textContent.includes('Play') || 
            button.className.includes('play') || 
            button.title.includes('Play')) {
          playButton = button;
          break;
        }
      }

      if (playButton) {
        setDebug('Activating visualizer...');
        playButton.click();
        // Wait for visualizer to start
        await new Promise(resolve => setTimeout(resolve, 500));
      } else {
        throw new Error("Could not find play button to activate visualizer");
      }

      // 1. Get canvas and ensure it's active
      const canvas = visualizerRef.current.getCanvas();
      if (!canvas) {
        throw new Error("Canvas not found");
      }

      // 2. Get the existing audio element
      const audioElements = document.querySelectorAll('audio');
      if (audioElements.length === 0) {
        throw new Error("No audio element found");
      }
      const audioElement = audioElements[0];
      audioElementRef.current = audioElement;

      // 3. Setup audio context
      const audioContext = new (window.AudioContext || window.webkitAudioContext)();
      audioContextRef.current = audioContext;

      const audioSource = audioContext.createMediaElementSource(audioElement);
      const audioDestination = audioContext.createMediaStreamDestination();
      audioSource.connect(audioDestination);

      // 4. Setup canvas stream
      const stream = canvas.captureStream(QUALITY_PRESETS[selectedQuality].frameRate);
      const videoTracks = stream.getVideoTracks();
      const audioTracks = audioDestination.stream.getAudioTracks();

      if (videoTracks.length === 0 || audioTracks.length === 0) {
        throw new Error("Missing video or audio tracks");
      }

      // 5. Combine streams
      const combinedStream = new MediaStream([...videoTracks, ...audioTracks]);

      // 6. Setup MediaRecorder
      const selectedMimeType = EXPORT_FORMATS[selectedFormat].mimeType;
      if (!MediaRecorder.isTypeSupported(selectedMimeType)) {
        throw new Error(`Format ${EXPORT_FORMATS[selectedFormat].label} not supported`);
      }

      const mediaRecorder = new MediaRecorder(combinedStream, {
        mimeType: selectedMimeType,
        videoBitsPerSecond: QUALITY_PRESETS[selectedQuality].videoBitrate,
        audioBitsPerSecond: QUALITY_PRESETS[selectedQuality].audioBitrate
      });

      mediaRecorderRef.current = mediaRecorder;

      // 7. Setup data collection with more frequent chunks
      mediaRecorder.ondataavailable = (event) => {
        if (event.data && event.data.size > 0) {
          chunksRef.current.push(event.data);
          const totalSize = chunksRef.current.reduce((size, chunk) => size + chunk.size, 0);
          setDebug(`Chunk received: ${(event.data.size / 1024).toFixed(2)} KB, Total: ${(totalSize / 1024 / 1024).toFixed(2)} MB, Chunks: ${chunksRef.current.length}`);
        } else {
          setDebug('Warning: Received empty data chunk');
        }
      };

      // 8. Setup recording stop handler
      mediaRecorder.onstop = () => {
        const totalSize = chunksRef.current.reduce((size, chunk) => size + chunk.size, 0);
        setDebug(`Recording stopped. Total chunks: ${chunksRef.current.length}, Total size: ${(totalSize / 1024 / 1024).toFixed(2)} MB`);

        if (totalSize === 0) {
          setDebug('Error: No data was recorded. Check if the visualizer is active and rendering.');
          throw new Error("No data was recorded. Check if the visualizer is active and rendering.");
        }

        const blob = new Blob(chunksRef.current, { type: selectedMimeType });
        const url = URL.createObjectURL(blob);

        let fileName = "visualization." + EXPORT_FORMATS[selectedFormat].extension;
        if (audioFile && audioFile.name) {
          fileName = audioFile.name.replace(/\.[^/.]+$/, "") + "_visualization." + EXPORT_FORMATS[selectedFormat].extension;
        }

        const a = document.createElement('a');
        a.href = url;
        a.download = fileName;
        a.click();

        URL.revokeObjectURL(url);
        setIsExporting(false);
        setProgress(100);
        setDebug('Export completed successfully');

        cleanupResources();
      };

      // 9. Start recording with more frequent chunks
      mediaRecorder.start(100); // Request data every 100ms
      setDebug('MediaRecorder started');

      // 10. Setup progress tracking
      audioDurationRef.current = audioElement.duration;
      progressIntervalRef.current = setInterval(() => {
        if (audioDurationRef.current) {
          const elapsed = (Date.now() - startTimeRef.current) / 1000;
          const percentage = Math.min(Math.floor((elapsed / audioDurationRef.current) * 100), 99);
          setProgress(percentage);
          setDebug(`Progress: ${percentage}%, Duration: ${elapsed.toFixed(1)}s/${audioDurationRef.current.toFixed(1)}s, Chunks: ${chunksRef.current.length}`);

          // Check if we're receiving chunks
          if (elapsed > 1 && chunksRef.current.length === 0) {
            setDebug('Warning: No chunks received after 1 second. Checking visualizer state...');
            // Force a data request
            if (mediaRecorder.state === 'recording') {
              mediaRecorder.requestData();
            }
          }
        }
      }, 500);

      // 11. Setup audio end handler
      audioElement.onended = () => {
        setDebug('Audio playback ended, stopping recording');
        if (mediaRecorder.state === 'recording') {
          mediaRecorder.requestData();
          setTimeout(() => mediaRecorder.stop(), 500);
        }
      };

    } catch (error) {
      console.error("Error exporting video:", error);
      setDebug(`Export error: ${error.message}`);
      alert(`Error exporting video: ${error.message}`);
      setIsExporting(false);
      cleanupResources();
    }
  };

  return (
    <div className="video-exporter">
      {isExporting ? (
        <div className="export-progress">
          <div className="progress-bar">
            <div className="progress-fill" style={{ width: `${progress}%` }}></div>
          </div>
          <div className="progress-text">{progress}% Exporting...</div>
          <div className="debug-info">{debug}</div>
        </div>
      ) : (
        <div className="export-controls">
          <select 
            value={selectedQuality} 
            onChange={(e) => setSelectedQuality(e.target.value)}
            className="quality-select"
          >
            {Object.entries(QUALITY_PRESETS).map(([key, preset]) => (
              <option key={key} value={key}>{preset.label}</option>
            ))}
          </select>
          <select 
            value={selectedFormat} 
            onChange={(e) => setSelectedFormat(e.target.value)}
            className="format-select"
            disabled={supportedFormats.length <= 1}
          >
            {Object.entries(EXPORT_FORMATS)
              .filter(([key]) => supportedFormats.includes(key))
              .map(([key, format]) => (
                <option key={key} value={key}>{format.label}</option>
              ))}
          </select>
          <button
            className="export-button"
            onClick={exportVideo}
            disabled={!visualizerRef || !audioFile || isPlaying || supportedFormats.length === 0}
          >
            Export Video
          </button>
        </div>
      )}
    </div>
  );
};

export default VideoExporter;
