import React, { useRef, useState, useEffect } from 'react';
import './VideoExporter.css';

const VideoExporter = ({ visualizerRef, audioFile, isPlaying }) => {
  const [isExporting, setIsExporting] = useState(false);
  const [progress, setProgress] = useState(0);
  const [debug, setDebug] = useState('');
  const mediaRecorderRef = useRef(null);
  const chunksRef = useRef([]);
  const startTimeRef = useRef(null);
  const audioDurationRef = useRef(null);
  const progressIntervalRef = useRef(null);
  const dataRequestIntervalRef = useRef(null);
  const audioContextRef = useRef(null);
  const audioSourceRef = useRef(null);
  const audioElementRef = useRef(null);

  // High quality settings
  const HIGH_VIDEO_BITRATE = 8000000; // 8 Mbps for high quality video
  const HIGH_AUDIO_BITRATE = 320000;  // 320 kbps for high quality audio
  const FRAME_RATE = 60;              // 60 fps for smooth animation

