  useEffect(() => {
    return () => {
      cleanupResources();
    };
  }, []);

  // Clean up resources function to centralize cleanup logic
  const cleanupResources = () => {
    if (progressIntervalRef.current) {
      clearInterval(progressIntervalRef.current);
      progressIntervalRef.current = null;
    }

    if (dataRequestIntervalRef.current) {
      clearInterval(dataRequestIntervalRef.current);
      dataRequestIntervalRef.current = null;
    }

    if (audioSourceRef.current) {
      try {
        audioSourceRef.current.disconnect();
      } catch (e) {
        console.error('Error disconnecting audio source:', e);
      }
      audioSourceRef.current = null;
    }

    if (audioElementRef.current) {
      try {
        audioElementRef.current.pause();
        // Remove all event listeners
        audioElementRef.current.oncanplaythrough = null;
        audioElementRef.current.onerror = null;
        audioElementRef.current.onended = null;
        // Clear source and load to release resources
        audioElementRef.current.removeAttribute('src');
        audioElementRef.current.load();
      } catch (e) {
        console.error('Error cleaning up audio element:', e);
      }
      audioElementRef.current = null;
    }

    if (audioContextRef.current && audioContextRef.current.state !== 'closed') {
      try {
        audioContextRef.current.close().catch(e => console.error('Error closing audio context:', e));
      } catch (e) {
        console.error('Error closing audio context:', e);
      }
      audioContextRef.current = null;
    }

    if (mediaRecorderRef.current && mediaRecorderRef.current.state === 'recording') {
      try {
        mediaRecorderRef.current.stop();
      } catch (e) {
        console.error('Error stopping media recorder:', e);
      }
      mediaRecorderRef.current = null;
    }

    chunksRef.current = [];
  };

  // Validate that the audio file path is valid
  const validateAudioFile = () => {
    if (!audioFile) {
      return false;
    }

    if (!audioFile.path) {
      console.error('Audio file has no path');
      setDebug('Error: Audio file has no path');
      return false;
    }

    return true;
  };

  // Function to ensure the visualizer is active and rendering
  const ensureVisualizerActive = () => {
    if (!visualizerRef?.current) {
      console.error('Visualizer reference not available');
      return false;
    }

    // Force a render cycle on the canvas to ensure it's active
    const canvas = visualizerRef.current.getCanvas();
    if (!canvas) {
      console.error('Canvas not found in visualizer');
      return false;
    }

    try {
      // Force a frame to be drawn on the canvas to ensure it has content
      const ctx = canvas.getContext('2d');
      const imageData = ctx.getImageData(0, 0, canvas.width, canvas.height);
      ctx.putImageData(imageData, 0, 0);
      return true;
    } catch (e) {
      console.error('Error activating visualizer:', e);
      return false;
    }
  };

  // Get the current canvas resolution
  const getCanvasResolution = (canvas) => {
    if (!canvas) return { width: 0, height: 0 };

    // Get the actual canvas dimensions
    const width = canvas.width;
    const height = canvas.height;

    // Log the resolution
    console.log(`Canvas resolution: ${width}x${height}`);
    setDebug(`Canvas resolution: ${width}x${height}`);

    return { width, height };
  };

  const exportVideo = async () => {
    if (!visualizerRef?.current || !audioFile) {
      alert("Please upload an audio file first");
      return;
    }

    // Validate audio file
    if (!validateAudioFile()) {
      alert("Invalid audio file. Please upload a valid audio file.");
      return;
    }

    // Check if already exporting
    if (isExporting) {
      console.log('Already exporting, ignoring request');
      return;
    }

    try {
      // Clean up any existing resources first
      cleanupResources();

      setIsExporting(true);
      setProgress(0);
      setDebug('Starting export process...');
      startTimeRef.current = Date.now();

      // Ensure visualizer is active
      if (!ensureVisualizerActive()) {
        throw new Error("Could not activate visualizer");
      }

      // Get the canvas element from the visualizer using the exposed method
      const canvas = visualizerRef.current.getCanvas();
      if (!canvas) {
        throw new Error("Canvas element not found");
      }

      // Get and log the canvas resolution
      const resolution = getCanvasResolution(canvas);
      setDebug(`Canvas found, dimensions: ${resolution.width}x${resolution.height}`);

      // Ensure the canvas has a reasonable size for high-quality export
      if (resolution.width < 720 || resolution.height < 720) {
        setDebug(`Warning: Canvas resolution (${resolution.width}x${resolution.height}) is low. Consider using a higher resolution for better quality.`);
      }

      // Create a stream from the canvas with high frame rate
      let stream = null;

      // Method 1: Standard captureStream with high frame rate
      try {
        stream = canvas.captureStream(FRAME_RATE);
        setDebug(`Canvas stream created with ${FRAME_RATE}fps`);
      } catch (e) {
        console.error('Standard captureStream failed:', e);
        setDebug('Standard captureStream failed, trying alternatives...');
      }

      // Method 2: Mozilla captureStream
      if (!stream) {
        try {
          stream = canvas.mozCaptureStream && canvas.mozCaptureStream(FRAME_RATE);
          if (stream) {
            setDebug(`Canvas stream created with mozCaptureStream at ${FRAME_RATE}fps`);
          }
        } catch (e) {
          console.error('mozCaptureStream failed:', e);
          setDebug('mozCaptureStream failed, trying alternatives...');
        }
      }

      // Method 3: WebKit captureStream
      if (!stream) {
        try {
          stream = canvas.webkitCaptureStream && canvas.webkitCaptureStream(FRAME_RATE);
          if (stream) {
            setDebug(`Canvas stream created with webkitCaptureStream at ${FRAME_RATE}fps`);
          }
        } catch (e) {
          console.error('webkitCaptureStream failed:', e);
          setDebug('webkitCaptureStream failed');
        }
      }

      if (!stream) {
        throw new Error("Your browser doesn't support canvas streaming. Try using a different browser like Chrome.");
      }

      // Verify we have video tracks
      const videoTracks = stream.getVideoTracks();
      if (videoTracks.length === 0) {
        throw new Error("No video tracks found in canvas stream");
      }
      setDebug(`Stream created with ${videoTracks.length} video tracks`);

      // Log video track details and try to set constraints for higher quality
      videoTracks.forEach((track, index) => {
        console.log(`Video track ${index}:`, track.label, track.enabled, track.readyState);

        // Try to set constraints for higher quality if supported
        try {
          if (track.applyConstraints) {
            track.applyConstraints({
              width: { ideal: resolution.width },
              height: { ideal: resolution.height },
              frameRate: { ideal: FRAME_RATE }
            });
            console.log(`Applied constraints to video track ${index}`);
          }
        } catch (e) {
          console.error('Could not apply constraints to video track:', e);
        }
      });

      // Create audio context and connect to stream - use a more reliable approach
      try {
        // Create a new audio context with high sample rate
        const AudioContext = window.AudioContext || window.webkitAudioContext;
        const audioContext = new AudioContext({ sampleRate: 48000 });
        audioContextRef.current = audioContext;

        // Construct the audio URL with proper encoding
        const timestamp = new Date().getTime();
        // Ensure the path is properly encoded
        const encodedPath = encodeURI(audioFile.path);
        const audioUrl = `http://localhost:5001${encodedPath}?t=${timestamp}`;

        console.log('Audio URL:', audioUrl);
        setDebug(`Using audio URL: ${audioUrl}`);

        // Create a new audio element for playback during recording
        const audioElement = new Audio();
        audioElement.crossOrigin = "anonymous";
        audioElement.preload = "auto";
        audioElementRef.current = audioElement;

        // Set up error handler before setting source
        audioElement.onerror = (e) => {
          const errorDetail = e.target.error 
            ? `${e.target.error.message || ''} (code: ${e.target.error.code})` 
            : 'Unknown error';
          console.error('Audio error:', e.target.error);
          setDebug(`Audio error: ${errorDetail}`);
          throw new Error(`Audio error: ${errorDetail}`);
        };

        // Wait for audio to be loaded enough to play
        setDebug('Loading audio...');

        // Create a promise to handle audio loading
        const audioLoadPromise = new Promise((resolve, reject) => {
          // Set up success handler
          audioElement.oncanplaythrough = () => {
            console.log('Audio loaded successfully, duration:', audioElement.duration);
            setDebug('Audio loaded successfully');
            resolve();
          };

          // Set up error handler
          audioElement.onerror = (e) => {
            const errorDetail = e.target.error 
              ? `${e.target.error.message || ''} (code: ${e.target.error.code})` 
              : 'Unknown error';
            console.error('Audio load error:', e.target.error);
            setDebug(`Audio load error: ${errorDetail}`);
            reject(new Error(`Audio load error: ${errorDetail}`));
          };

          // Set a timeout in case the audio never loads
          const timeoutId = setTimeout(() => {
            audioElement.oncanplaythrough = null;
            audioElement.onerror = null;
            setDebug('Audio load timeout - took too long to load');
            reject(new Error('Audio load timeout - took too long to load'));
          }, 10000);

          // Clean up timeout if audio loads or errors
          const clearTimeoutHandler = () => {
            clearTimeout(timeoutId);
          };

          audioElement.addEventListener('canplaythrough', clearTimeoutHandler, { once: true });
          audioElement.addEventListener('error', clearTimeoutHandler, { once: true });
        });

        // Set source AFTER setting up all event handlers
        audioElement.src = audioUrl;

        // Start loading
        audioElement.load();

        // Wait for audio to load
        await audioLoadPromise;

        // Store the duration for progress calculation
        audioDurationRef.current = audioElement.duration;
        setDebug(`Audio duration: ${audioDurationRef.current.toFixed(2)} seconds`);

        // Create and connect the audio source
        const audioSource = audioContext.createMediaElementSource(audioElement);
        audioSourceRef.current = audioSource;

        // Create a destination with high-quality settings
        const audioDestination = audioContext.createMediaStreamDestination();

        // Optional: Add audio processing for better quality
        const gainNode = audioContext.createGain();
        gainNode.gain.value = 1.0; // Ensure full volume

        // Connect the audio processing chain
        audioSource.connect(gainNode);
        gainNode.connect(audioDestination);
        gainNode.connect(audioContext.destination); // Also connect to speakers

        // Verify we have audio tracks
        const audioTracks = audioDestination.stream.getAudioTracks();
        if (audioTracks.length === 0) {
          setDebug('Warning: No audio tracks found in audio stream');
        } else {
          setDebug(`Audio stream created with ${audioTracks.length} audio tracks`);

          // Log audio track details
          audioTracks.forEach((track, index) => {
            console.log(`Audio track ${index}:`, track.label, track.enabled, track.readyState);
          });
        }

        // Combine video and audio streams
        const combinedStream = new MediaStream([
          ...videoTracks,
          ...audioTracks
        ]);

        // Check if combined stream has tracks
        if (combinedStream.getTracks().length === 0) {
          throw new Error("Combined stream has no tracks");
        }
        setDebug(`Combined stream created with ${combinedStream.getTracks().length} total tracks`);

        // Try different MIME types in order of preference for high quality
        const mimeTypes = [
          'video/webm;codecs=vp9,opus', // VP9 is higher quality than VP8
          'video/webm;codecs=h264,opus', // H.264 is widely supported
          'video/webm;codecs=vp8,opus', // VP8 fallback
          'video/webm' // Generic fallback
        ];

        let selectedMimeType = '';
        for (const type of mimeTypes) {
          if (MediaRecorder.isTypeSupported(type)) {
            selectedMimeType = type;
            setDebug(`Using MIME type: ${selectedMimeType}`);
            break;
          }
        }

        if (!selectedMimeType) {
          setDebug('Warning: No standard MIME types supported, trying without specifying codec');
        }

        // Create MediaRecorder with high quality options
        const options = {
          mimeType: selectedMimeType || 'video/webm',
          videoBitsPerSecond: HIGH_VIDEO_BITRATE,
          audioBitsPerSecond: HIGH_AUDIO_BITRATE
        };

        try {
          const mediaRecorder = new MediaRecorder(combinedStream, options);
          mediaRecorderRef.current = mediaRecorder;

          // Log MediaRecorder details
          console.log('MediaRecorder created:', mediaRecorder.mimeType, mediaRecorder.state);
          setDebug(`MediaRecorder created with mime type: ${mediaRecorder.mimeType}, video bitrate: ${HIGH_VIDEO_BITRATE/1000000}Mbps`);

          // Log MediaRecorder state changes
          mediaRecorder.addEventListener('start', () => {
            console.log('MediaRecorder started');
            setDebug('Recording started at high quality');
          });

          mediaRecorder.addEventListener('error', (event) => {
            console.error('MediaRecorder error:', event);
            setDebug(`MediaRecorder error: ${event.error || 'Unknown error'}`);
          });

          // Handle data available - this is critical
          mediaRecorder.ondataavailable = (event) => {
            console.log('Data available event, size:', event.data.size);
            if (event.data && event.data.size > 0) {
              chunksRef.current.push(event.data);
              setDebug(`Received data chunk: ${(event.data.size / 1024).toFixed(2)} KB, total chunks: ${chunksRef.current.length}`);
            } else {
              console.warn('Received empty data chunk');
              setDebug('Warning: Received empty data chunk');
            }
          };

          // Handle recording stop
          mediaRecorder.onstop = () => {
            console.log('MediaRecorder stopped, chunks:', chunksRef.current.length);
            setDebug(`Recording stopped with ${chunksRef.current.length} chunks`);

            if (chunksRef.current.length === 0) {
              setDebug('Error: No data chunks were recorded');
              alert('Error: No video data was captured during recording. This may be due to browser compatibility issues. Try using Chrome or Edge.');
              setIsExporting(false);
              cleanupResources();
              return;
            }

            // Calculate total size of all chunks
            const totalSize = chunksRef.current.reduce((size, chunk) => size + chunk.size, 0);
            setDebug(`Total data size: ${(totalSize / 1024 / 1024).toFixed(2)} MB`);

            if (totalSize === 0) {
              setDebug('Error: Recorded data has zero size');
              alert('Error: The recorded video has no data (0 bytes). Try using a different browser or visualization.');
              setIsExporting(false);
              cleanupResources();
              return;
            }

            try {
              // Create blob from chunks with high quality settings
              const blob = new Blob(chunksRef.current, { type: selectedMimeType || 'video/webm' });
              console.log('Created blob, size:', blob.size);
              setDebug(`Created video blob: ${(blob.size / 1024 / 1024).toFixed(2)} MB`);

              if (blob.size === 0) {
                setDebug('Error: Final blob has zero size');
                alert('Error: The final video file has no data (0 bytes). Try using a different browser.');
                setIsExporting(false);
                cleanupResources();
                return;
              }

              const url = URL.createObjectURL(blob);

              // Create filename based on audio file name with fallback
              let fileName = "visualization.webm";
              if (audioFile && audioFile.name) {
                fileName = audioFile.name.replace(/\.[^/.]+$/, "") + "_visualization.webm";
              } else if (audioFile && audioFile.path) {
                // Extract filename from path as fallback
                const pathParts = audioFile.path.split('/');
                const pathFileName = pathParts[pathParts.length - 1];
                fileName = pathFileName.replace(/\.[^/.]+$/, "") + "_visualization.webm";
              }

              // Download the file
              const a = document.createElement('a');
              a.href = url;
              a.download = fileName;
              a.click();

              // Clean up
              URL.revokeObjectURL(url);
              setIsExporting(false);
              setProgress(100);
              setDebug('Export completed successfully');

              // Clean up resources
              cleanupResources();
            } catch (blobError) {
              console.error('Error creating blob:', blobError);
              setDebug(`Error creating video file: ${blobError.message}`);
              alert(`Error creating video file: ${blobError.message}`);
              setIsExporting(false);
              cleanupResources();
            }
          };

          // Use smaller timeslice for higher quality
          mediaRecorder.start(100); // Capture in 100ms chunks for more frequent data
          setDebug('MediaRecorder started with 100ms timeslice for high quality');

          // Start audio playback and ensure visualization is active
          try {
            // Start audio playback
            await audioElement.play();
            setDebug('Audio playback started');

            // Ensure the visualizer is active and responding to the audio
            // This is critical - we need to make sure the visualizer is actually rendering
            // when the audio plays, otherwise we'll just record a static canvas
            if (visualizerRef.current && typeof visualizerRef.current.startVisualization === 'function') {
              visualizerRef.current.startVisualization();
              setDebug('Visualizer activated');
            } else {
              // If there's no direct method, we can try to simulate a play event
              // This is a fallback approach
              const playEvent = new Event('play');
              audioElement.dispatchEvent(playEvent);
              setDebug('Dispatched play event to trigger visualization');
            }

            // Force a render cycle to ensure the canvas is updating
            if (canvas) {
              const ctx = canvas.getContext('2d');
              ctx.fillStyle = 'rgba(0,0,0,0.01)';
              ctx.fillRect(0, 0, 1, 1); // Minimal draw to force a render cycle
            }

          } catch (playError) {
            console.error('Error playing audio:', playError);
            setDebug(`Failed to play audio: ${playError.message || 'Unknown error'}`);
            throw new Error(`Failed to play audio: ${playError.message}`);
          }

          // Periodically request data to ensure chunks are being collected
          dataRequestIntervalRef.current = setInterval(() => {
            if (mediaRecorderRef.current && mediaRecorderRef.current.state === 'recording') {
              mediaRecorderRef.current.requestData();

              // Also periodically check if the canvas is still active
              if (canvas) {
                try {
                  const ctx = canvas.getContext('2d');
                  ctx.fillStyle = 'rgba(0,0,0,0.01)';
                  ctx.fillRect(0, 0, 1, 1); // Minimal draw to keep the canvas active
                } catch (e) {
                  console.error('Error keeping canvas active:', e);
                }
              }
            }
          }, 1000);

          // Update progress based on audio time
          progressIntervalRef.current = setInterval(() => {
            if (audioDurationRef.current) {
              const elapsed = (Date.now() - startTimeRef.current) / 1000;
              const percentage = Math.min(Math.floor((elapsed / audioDurationRef.current) * 100), 99);
              setProgress(percentage);

              // Log data chunks periodically
              setDebug(`Progress: ${percentage}%, Chunks: ${chunksRef.current.length}, Duration: ${elapsed.toFixed(1)}s/${audioDurationRef.current.toFixed(1)}s`);

              // If we've been recording for a while but have no chunks, something is wrong
              if (elapsed > 3 && chunksRef.current.length === 0) {
                setDebug('Warning: No data chunks received after 3 seconds');
              }
            }
          }, 500);

          // Stop recording when audio ends
          audioElement.onended = () => {
            setDebug('Audio playback ended, stopping recording');

            // Clear the data request interval
            if (dataRequestIntervalRef.current) {
              clearInterval(dataRequestIntervalRef.current);
              dataRequestIntervalRef.current = null;
            }

            if (mediaRecorderRef.current && mediaRecorderRef.current.state === 'recording') {
              // Force one last dataavailable event before stopping
              mediaRecorderRef.current.requestData();

              // Small delay to ensure the last data is captured
              setTimeout(() => {
                try {
                  mediaRecorderRef.current.stop();
                } catch (stopError) {
                  console.error('Error stopping MediaRecorder:', stopError);
                  setDebug(`Error stopping recording: ${stopError.message}`);
                  setIsExporting(false);
                  cleanupResources();
                }
              }, 500);
            }
          };

        } catch (recorderError) {
          console.error('Error creating MediaRecorder:', recorderError);
          setDebug(`Failed to create MediaRecorder: ${recorderError.message}`);
          throw new Error(`Failed to create MediaRecorder: ${recorderError.message}. Try using a different browser.`);
        }
      } catch (audioError) {
        console.error('Error setting up audio:', audioError);
        setDebug(`Audio setup error: ${audioError.message}`);
        throw new Error(`Failed to set up audio: ${audioError.message}`);
      }

    } catch (error) {
      console.error("Error exporting video:", error);
      setDebug(`Export error: ${error.message || 'Unknown error'}`);
      alert(`Error exporting video: ${error.message}`);
      setIsExporting(false);
      cleanupResources();
    }
  };

  return (
    <div className="video-exporter">
      {isExporting ? (
        <div className="export-progress">
          <div className="progress-bar">
            <div className="progress-fill" style={{ width: `${progress}%` }}></div>
          </div>
          <div className="progress-text">{progress}% Exporting...</div>
          <div className="debug-info">{debug}</div>
        </div>
      ) : (
        <button
          className="export-button"
          onClick={exportVideo}
          disabled={!visualizerRef || !audioFile || isPlaying}
        >
          Export Video
        </button>
      )}
    </div>
  );
};

export default VideoExporter;
