        const audioValue = audioData.dataArray[audioIndex] * 0.1;
        
        positions[i * 3] = originalPositions[i * 3] * (1 + audioValue);
        positions[i * 3 + 1] = originalPositions[i * 3 + 1] * (1 + audioValue);
      }
      
      particlesRef.current.geometry.attributes.position.needsUpdate = true;
    }
    
    // Update kaleidoscope if available
    if (particlesRef.current && template === 'kaleidoscope' && audioData && audioData.dataArray) {
      let avgAudio = 0;
      
      for (let i = 0; i < 128; i++) {
        avgAudio += audioData.dataArray[i];
      }
      
      avgAudio /= 128;
      
      particlesRef.current.rotation.x += 0.005;
      particlesRef.current.rotation.y += 0.01;
      particlesRef.current.scale.set(1 + avgAudio * 0.2, 1 + avgAudio * 0.2, 1 + avgAudio * 0.2);
    }
    
    // Update controls
    if (controlsRef.current) {
      controlsRef.current.update();
    }
    
    // Render scene
    rendererRef.current.render(sceneRef.current, cameraRef.current);
  };

  return <div ref={mountRef} style={{ width: '100%', height: '100%' }} />;
};

export default ThreeVisualizer;
