import React, { useRef, useState, useEffect } from 'react';
import './VideoExporter.css';

// Import RESOLUTIONS from Visualizer
const RESOLUTIONS = {
  '4K': { width: 3840, height: 2160, className: 'aspect-ratio-4k' },
  '1080p': { width: 1920, height: 1080, className: 'aspect-ratio-1080p' },
  '720p': { width: 1280, height: 720, className: 'aspect-ratio-720p' },
  'Square': { width: 720, height: 720, className: 'aspect-ratio-square' },
  'Vertical': { width: 1080, height: 1920, className: 'aspect-ratio-vertical' }
};

const VideoExporter = ({ visualizerRef, audioFile, isPlaying }) => {
  const [isExporting, setIsExporting] = useState(false);
  const [progress, setProgress] = useState(0);
  const [error, setError] = useState(null);
  
  const mediaRecorderRef = useRef(null);
  const chunksRef = useRef([]);
  const startTimeRef = useRef(null);
  const audioContextRef = useRef(null);
  const audioSourceRef = useRef(null);
  const audioDestinationRef = useRef(null);
  const streamRef = useRef(null);
  const audioElementRef = useRef(null);

  // Store audio element reference when it's created
  useEffect(() => {
    const audioElements = document.querySelectorAll('audio');
    if (audioElements.length > 0) {
      audioElementRef.current = audioElements[0];
    }
  }, [audioFile]);

  // Cleanup function to ensure all resources are properly released
  const cleanup = () => {
    if (mediaRecorderRef.current?.state === 'recording') {
      mediaRecorderRef.current.stop();
      mediaRecorderRef.current = null;
    }
    
    if (streamRef.current) {
      streamRef.current.getTracks().forEach(track => track.stop());
      streamRef.current = null;
    }
    
    if (audioSourceRef.current) {
      audioSourceRef.current.disconnect();
      audioSourceRef.current = null;
    }
    
    if (audioDestinationRef.current) {
      audioDestinationRef.current.disconnect();
      audioDestinationRef.current = null;
    }
    
    if (audioContextRef.current?.state !== 'closed') {
      audioContextRef.current?.close();
      audioContextRef.current = null;
    }
    
    chunksRef.current = [];
  };

  // Cleanup on unmount
  useEffect(() => {
    return cleanup;
  }, []);

  const exportVideo = async () => {
    if (!visualizerRef?.current || !audioFile) {
      setError('Please upload an audio file first');
      return;
    }

    if (isExporting) {
      setError('Export already in progress');
      return;
    }

    try {
      cleanup();
      setIsExporting(true);
      setProgress(0);
      setError(null);
      startTimeRef.current = Date.now();

      // Get the canvas and its current resolution
      const canvas = visualizerRef.current.getCanvas();
      if (!canvas) {
        throw new Error('Canvas not found');
      }

      // Get the current resolution from the visualizer
      const resolution = visualizerRef.current.getCurrentResolution();
      const { width, height } = RESOLUTIONS[resolution];

      // Set canvas to full resolution for export
      const originalWidth = canvas.width;
      const originalHeight = canvas.height;
      canvas.width = width;
      canvas.height = height;

      // Get the audio element from our ref
      const audioElement = audioElementRef.current;
      if (!audioElement) {
        throw new Error('Audio element not found. Please ensure audio is loaded and playing.');
      }

      // Ensure audio is ready
      if (audioElement.readyState < 2) {
        await new Promise((resolve, reject) => {
          audioElement.oncanplaythrough = resolve;
          audioElement.onerror = reject;
        });
      }

      // Setup audio context and nodes
      const audioContext = new (window.AudioContext || window.webkitAudioContext)();
      audioContextRef.current = audioContext;

      const audioSource = audioContext.createMediaElementSource(audioElement);
      audioSourceRef.current = audioSource;

      const audioDestination = audioContext.createMediaStreamDestination();
      audioDestinationRef.current = audioDestination;

      audioSource.connect(audioDestination);

      // Setup canvas stream
      const canvasStream = canvas.captureStream(60); // 60 FPS for highest quality
      streamRef.current = canvasStream;

      // Combine video and audio streams
      const combinedStream = new MediaStream([
        ...canvasStream.getVideoTracks(),
        ...audioDestination.stream.getAudioTracks()
      ]);

      // Setup MediaRecorder with highest quality settings
      const mediaRecorder = new MediaRecorder(combinedStream, {
        mimeType: 'video/webm;codecs=vp9,opus',
        videoBitsPerSecond: 8000000, // 8 Mbps
        audioBitsPerSecond: 320000    // 320 kbps
      });

      mediaRecorderRef.current = mediaRecorder;

      // Handle data collection
      mediaRecorder.ondataavailable = (event) => {
        if (event.data && event.data.size > 0) {
          chunksRef.current.push(event.data);
        }
      };

      // Handle recording completion
      mediaRecorder.onstop = () => {
        const blob = new Blob(chunksRef.current, { type: 'video/webm' });
        const url = URL.createObjectURL(blob);

        // Generate filename based on audio file name
        const fileName = audioFile.name.replace(/\.[^/.]+$/, '') + '_visualization.webm';

        // Create download link
        const a = document.createElement('a');
        a.href = url;
        a.download = fileName;
        a.click();

        // Cleanup
        URL.revokeObjectURL(url);
        setIsExporting(false);
        setProgress(100);

        // Restore original canvas size
        canvas.width = originalWidth;
        canvas.height = originalHeight;

        cleanup();
      };

      // Start recording
      mediaRecorder.start(1000); // Collect data every second
      setProgress(1);

      // Update progress
      const progressInterval = setInterval(() => {
        if (audioElement.duration) {
          const elapsed = (Date.now() - startTimeRef.current) / 1000;
          const percentage = Math.min(Math.floor((elapsed / audioElement.duration) * 100), 99);
          setProgress(percentage);
        }
      }, 100);

      // Handle audio end
      audioElement.onended = () => {
        clearInterval(progressInterval);
        if (mediaRecorder.state === 'recording') {
          mediaRecorder.stop();
        }
      };

    } catch (error) {
      console.error('Export error:', error);
      setError(error.message);
      setIsExporting(false);
      cleanup();
    }
  };

  return (
    <div className="video-exporter">
      {isExporting ? (
        <div className="export-progress">
          <div className="progress-bar">
            <div className="progress-fill" style={{ width: `${progress}%` }}></div>
          </div>
          <div className="progress-text">{progress}% Exporting...</div>
        </div>
      ) : (
        <button
          className="export-button"
          onClick={exportVideo}
          disabled={!visualizerRef || !audioFile || isPlaying || !audioElementRef.current}
        >
          Export Video
        </button>
      )}
      {error && <div className="error-message">{error}</div>}
    </div>
  );
};

export default VideoExporter;
