import React, { useEffect, useRef, forwardRef } from 'react';
import './Visualizer.css';

const Visualizer = forwardRef(({ audioFile, template, isPlaying }, ref) => {
  const canvasRef = useRef(null);
  const audioContextRef = useRef(null);
  const analyserRef = useRef(null);
  const animationFrameRef = useRef(null);
  const sourceRef = useRef(null);
  const audioElementRef = useRef(null);

  useEffect(() => {
    if (!audioFile) return;

    const initAudio = async () => {
      try {
        // Create new audio context if it doesn't exist
        if (!audioContextRef.current) {
          audioContextRef.current = new (window.AudioContext || window.webkitAudioContext)();
        }

        // Create analyzer if it doesn't exist
        if (!analyserRef.current) {
          analyserRef.current = audioContextRef.current.createAnalyser();
          analyserRef.current.fftSize = 2048;
        }

        // Disconnect any existing source
        if (sourceRef.current) {
          sourceRef.current.disconnect();
          sourceRef.current = null;
        }

        // Create and set up audio element
        const audioElement = new Audio();
        audioElement.crossOrigin = "anonymous"; // Enable CORS
        audioElementRef.current = audioElement;

        // Set up error handling
        audioElement.onerror = (e) => {
          console.error('Audio element error:', e);
          throw new Error(`Failed to load audio: ${audioElement.error?.message || 'Unknown error'}`);
        };

        // Set the audio source with cache-busting
        const timestamp = new Date().getTime();
        audioElement.src = `http://localhost:5001${audioFile.path}?t=${timestamp}`;

        // Wait for audio to be ready
        await new Promise((resolve, reject) => {
          const timeout = setTimeout(() => {
            reject(new Error('Audio loading timed out'));
          }, 10000);

          const handleCanPlay = () => {
            clearTimeout(timeout);
            resolve();
          };

          audioElement.addEventListener('canplay', handleCanPlay, { once: true });
          audioElement.addEventListener('error', (e) => {
            clearTimeout(timeout);
            reject(new Error(`Audio loading failed: ${audioElement.error?.message || 'Unknown error'}`));
          }, { once: true });

          // Start loading the audio
          audioElement.load();
        });

        // Resume audio context if it's suspended
        if (audioContextRef.current.state === 'suspended') {
          await audioContextRef.current.resume();
        }

        // Create and connect the media source
        const mediaSource = audioContextRef.current.createMediaElementSource(audioElement);
        sourceRef.current = mediaSource;
        
        mediaSource.connect(analyserRef.current);
        analyserRef.current.connect(audioContextRef.current.destination);

        // Start playback if needed
        if (isPlaying) {
          try {
            await audioElement.play();
            startVisualization();
          } catch (playError) {
            console.error('Error playing audio:', playError);
            throw new Error('Failed to play audio. Please try again.');
          }
        }

        return () => {
          if (audioElement) {
            audioElement.pause();
            audioElement.src = '';
          }
          if (sourceRef.current) {
            sourceRef.current.disconnect();
          }
          cancelAnimationFrame(animationFrameRef.current);
        };
      } catch (error) {
        console.error('Error initializing audio:', error);
        alert(error.message || 'Error loading audio file. Please try a different file format or check if the file is corrupted.');
      }
    };

    initAudio();
  }, [audioFile, isPlaying]);

  useEffect(() => {
    if (!audioElementRef.current) return;

    if (isPlaying) {
      audioElementRef.current.play().catch(error => {
        console.error('Error playing audio:', error);
        alert('Failed to play audio. Please try again.');
      });
      startVisualization();
    } else {
      audioElementRef.current.pause();
      cancelAnimationFrame(animationFrameRef.current);
    }
  }, [isPlaying]);

  const startVisualization = () => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const analyser = analyserRef.current;
    const bufferLength = analyser.frequencyBinCount;
    const dataArray = new Uint8Array(bufferLength);

    const draw = () => {
      animationFrameRef.current = requestAnimationFrame(draw);
      analyser.getByteFrequencyData(dataArray);

      ctx.fillStyle = 'rgb(0, 0, 0)';
      ctx.fillRect(0, 0, canvas.width, canvas.height);

      switch (template) {
        case 'waveform':
          drawWaveform(ctx, dataArray, bufferLength);
          break;
        case 'bars':
          drawBars(ctx, dataArray, bufferLength);
          break;
        case 'circles':
          drawCircles(ctx, dataArray, bufferLength);
          break;
        default:
          drawWaveform(ctx, dataArray, bufferLength);
      }
    };

    draw();
  };

  const drawWaveform = (ctx, dataArray, bufferLength) => {
    ctx.lineWidth = 2;
    ctx.strokeStyle = 'rgb(0, 255, 0)';
    ctx.beginPath();

    const sliceWidth = ctx.canvas.width / bufferLength;
    let x = 0;

    for (let i = 0; i < bufferLength; i++) {
      const v = dataArray[i] / 128.0;
      const y = v * ctx.canvas.height / 2;

      if (i === 0) {
        ctx.moveTo(x, y);
      } else {
        ctx.lineTo(x, y);
      }

      x += sliceWidth;
    }

    ctx.lineTo(ctx.canvas.width, ctx.canvas.height / 2);
    ctx.stroke();
  };

  const drawBars = (ctx, dataArray, bufferLength) => {
    const barWidth = (ctx.canvas.width / bufferLength) * 2.5;
    let barHeight;
    let x = 0;

    for (let i = 0; i < bufferLength; i++) {
      barHeight = dataArray[i] / 2;

      ctx.fillStyle = `rgb(${barHeight + 100}, 50, 50)`;
      ctx.fillRect(x, ctx.canvas.height - barHeight, barWidth, barHeight);

      x += barWidth + 1;
    }
  };

  const drawCircles = (ctx, dataArray, bufferLength) => {
    const centerX = ctx.canvas.width / 2;
    const centerY = ctx.canvas.height / 2;
    const maxRadius = Math.min(centerX, centerY) * 0.8;

    for (let i = 0; i < bufferLength; i++) {
      const radius = (dataArray[i] / 255) * maxRadius;
      const angle = (i / bufferLength) * Math.PI * 2;

      const x = centerX + Math.cos(angle) * radius;
      const y = centerY + Math.sin(angle) * radius;

      ctx.beginPath();
      ctx.arc(x, y, 2, 0, Math.PI * 2);
      ctx.fillStyle = `hsl(${(i / bufferLength) * 360}, 100%, 50%)`;
      ctx.fill();
    }
  };

  return (
    <canvas
      ref={(el) => {
        canvasRef.current = el;
        if (ref) ref.current = el;
      }}
      className="visualizer-canvas"
      width={800}
      height={400}
    />
  );
});

export default Visualizer;
