        throw new Error("Your browser doesn't support canvas streaming. Try using a different browser like Chrome.");
      }

      // Verify we have video tracks
      const videoTracks = stream.getVideoTracks();
      if (videoTracks.length === 0) {
        throw new Error("No video tracks found in canvas stream");
      }
      setDebug(`Stream created with ${videoTracks.length} video tracks`);

      // Log video track details
      videoTracks.forEach((track, index) => {
        console.log(`Video track ${index}:`, track.label, track.enabled, track.readyState);
      });

