        // Create a new audio context
        const AudioContext = window.AudioContext || window.webkitAudioContext;
        const audioContext = new AudioContext();
        audioContextRef.current = audioContext;

        // Construct the audio URL with proper encoding
        const timestamp = new Date().getTime();
        // Ensure the path is properly encoded
        const encodedPath = encodeURI(audioFile.path);
        const audioUrl = `http://localhost:5001${encodedPath}?t=${timestamp}`;

        console.log('Audio URL:', audioUrl);
        setDebug(`Using audio URL: ${audioUrl}`);

        // Create a new audio element for playback during recording
        const audioElement = new Audio();
        audioElement.crossOrigin = "anonymous";
        audioElement.preload = "auto";
        audioElementRef.current = audioElement;

        // Set up error handler before setting source
        audioElement.onerror = (e) => {
          const errorDetail = e.target.error 
            ? `${e.target.error.message || ''} (code: ${e.target.error.code})` 
            : 'Unknown error';
          console.error('Audio error:', e.target.error);
          setDebug(`Audio error: ${errorDetail}`);
          throw new Error(`Audio error: ${errorDetail}`);
        };

        // Wait for audio to be loaded enough to play
        setDebug('Loading audio...');

        // Create a promise to handle audio loading
        const audioLoadPromise = new Promise((resolve, reject) => {
          // Set up success handler
          audioElement.oncanplaythrough = () => {
            console.log('Audio loaded successfully, duration:', audioElement.duration);
            setDebug('Audio loaded successfully');
            resolve();
          };

          // Set up error handler
          audioElement.onerror = (e) => {
            const errorDetail = e.target.error 
              ? `${e.target.error.message || ''} (code: ${e.target.error.code})` 
              : 'Unknown error';
            console.error('Audio load error:', e.target.error);
            setDebug(`Audio load error: ${errorDetail}`);
            reject(new Error(`Audio load error: ${errorDetail}`));
          };

          // Set a timeout in case the audio never loads
          const timeoutId = setTimeout(() => {
            audioElement.oncanplaythrough = null;
            audioElement.onerror = null;
            setDebug('Audio load timeout - took too long to load');
            reject(new Error('Audio load timeout - took too long to load'));
          }, 10000);

          // Clean up timeout if audio loads or errors
          const clearTimeoutHandler = () => {
            clearTimeout(timeoutId);
          };

          audioElement.addEventListener('canplaythrough', clearTimeoutHandler, { once: true });
          audioElement.addEventListener('error', clearTimeoutHandler, { once: true });
        });

        // Set source AFTER setting up all event handlers
        audioElement.src = audioUrl;

        // Start loading
        audioElement.load();

        // Wait for audio to load
        await audioLoadPromise;

        // Store the duration for progress calculation
        audioDurationRef.current = audioElement.duration;
        setDebug(`Audio duration: ${audioDurationRef.current.toFixed(2)} seconds`);

        // Create and connect the audio source
        const audioSource = audioContext.createMediaElementSource(audioElement);
        audioSourceRef.current = audioSource;

        const audioDestination = audioContext.createMediaStreamDestination();
        audioSource.connect(audioDestination);
        audioSource.connect(audioContext.destination); // Also connect to speakers

        // Verify we have audio tracks
        const audioTracks = audioDestination.stream.getAudioTracks();
        if (audioTracks.length === 0) {
          setDebug('Warning: No audio tracks found in audio stream');
        } else {
          setDebug(`Audio stream created with ${audioTracks.length} audio tracks`);

          // Log audio track details
          audioTracks.forEach((track, index) => {
            console.log(`Audio track ${index}:`, track.label, track.enabled, track.readyState);
          });
        }

        // Combine video and audio streams
        const combinedStream = new MediaStream([
          ...videoTracks,
          ...audioTracks
        ]);

        // Check if combined stream has tracks
        if (combinedStream.getTracks().length === 0) {
          throw new Error("Combined stream has no tracks");
        }
        setDebug(`Combined stream created with ${combinedStream.getTracks().length} total tracks`);

        // Try different MIME types in order of preference
        const mimeTypes = [
          'video/webm;codecs=vp9,opus',
          'video/webm;codecs=vp8,opus',
          'video/webm;codecs=h264,opus',
          'video/webm;codecs=vp9',
          'video/webm;codecs=vp8',
          'video/webm'
        ];

        let selectedMimeType = '';
        for (const type of mimeTypes) {
          if (MediaRecorder.isTypeSupported(type)) {
            selectedMimeType = type;
            setDebug(`Using MIME type: ${selectedMimeType}`);
            break;
          }
        }

        if (!selectedMimeType) {
          setDebug('Warning: No standard MIME types supported, trying without specifying codec');
        }

        // Create MediaRecorder with appropriate options
        const options = selectedMimeType 
          ? { mimeType: selectedMimeType, videoBitsPerSecond: 2500000 }
          : { videoBitsPerSecond: 2500000 };

        try {
          const mediaRecorder = new MediaRecorder(combinedStream, options);
          mediaRecorderRef.current = mediaRecorder;

          // Log MediaRecorder details
          console.log('MediaRecorder created:', mediaRecorder.mimeType, mediaRecorder.state);
          setDebug(`MediaRecorder created with mime type: ${mediaRecorder.mimeType}`);

          // Log MediaRecorder state changes
          mediaRecorder.addEventListener('start', () => {
            console.log('MediaRecorder started');
            setDebug('Recording started');
          });

          mediaRecorder.addEventListener('error', (event) => {
            console.error('MediaRecorder error:', event);
            setDebug(`MediaRecorder error: ${event.error || 'Unknown error'}`);
          });

          // Handle data available - this is critical
          mediaRecorder.ondataavailable = (event) => {
            console.log('Data available event, size:', event.data.size);
            if (event.data && event.data.size > 0) {
              chunksRef.current.push(event.data);
              setDebug(`Received data chunk: ${(event.data.size / 1024).toFixed(2)} KB, total chunks: ${chunksRef.current.length}`);
            } else {
              console.warn('Received empty data chunk');
              setDebug('Warning: Received empty data chunk');
            }
          };

          // Handle recording stop
          mediaRecorder.onstop = () => {
            console.log('MediaRecorder stopped, chunks:', chunksRef.current.length);
            setDebug(`Recording stopped with ${chunksRef.current.length} chunks`);

            if (chunksRef.current.length === 0) {
              setDebug('Error: No data chunks were recorded');
              alert('Error: No video data was captured during recording. This may be due to browser compatibility issues. Try using Chrome or Edge.');
              setIsExporting(false);
              cleanupResources();
              return;
            }

            // Calculate total size of all chunks
            const totalSize = chunksRef.current.reduce((size, chunk) => size + chunk.size, 0);
            setDebug(`Total data size: ${(totalSize / 1024).toFixed(2)} KB`);

            if (totalSize === 0) {
              setDebug('Error: Recorded data has zero size');
              alert('Error: The recorded video has no data (0 bytes). Try using a different browser or visualization.');
              setIsExporting(false);
              cleanupResources();
              return;
            }

            try {
              // Create blob from chunks
              const blob = new Blob(chunksRef.current, { type: selectedMimeType || 'video/webm' });
              console.log('Created blob, size:', blob.size);
              setDebug(`Created video blob: ${(blob.size / 1024).toFixed(2)} KB`);

              if (blob.size === 0) {
                setDebug('Error: Final blob has zero size');
                alert('Error: The final video file has no data (0 bytes). Try using a different browser.');
                setIsExporting(false);
                cleanupResources();
                return;
              }

              const url = URL.createObjectURL(blob);

              // Create filename based on audio file name with fallback
              let fileName = "visualization.webm";
              if (audioFile && audioFile.name) {
                fileName = audioFile.name.replace(/\.[^/.]+$/, "") + "_visualization.webm";
              } else if (audioFile && audioFile.path) {
                // Extract filename from path as fallback
                const pathParts = audioFile.path.split('/');
                const pathFileName = pathParts[pathParts.length - 1];
                fileName = pathFileName.replace(/\.[^/.]+$/, "") + "_visualization.webm";
              }

              // Download the file
              const a = document.createElement('a');
              a.href = url;
              a.download = fileName;
              a.click();

              // Clean up
              URL.revokeObjectURL(url);
              setIsExporting(false);
              setProgress(100);
              setDebug('Export completed successfully');

              // Clean up resources
              cleanupResources();
            } catch (blobError) {
              console.error('Error creating blob:', blobError);
              setDebug(`Error creating video file: ${blobError.message}`);
              alert(`Error creating video file: ${blobError.message}`);
              setIsExporting(false);
              cleanupResources();
            }
          };

          // Force data collection more frequently
          mediaRecorder.start(100); // Capture in 100ms chunks for more frequent data
          setDebug('MediaRecorder started with 100ms timeslice');

          // Start audio playback
          try {
            await audioElement.play();
            setDebug('Audio playback started');
          } catch (playError) {
            console.error('Error playing audio:', playError);
            setDebug(`Failed to play audio: ${playError.message || 'Unknown error'}`);
            throw new Error(`Failed to play audio: ${playError.message}`);
          }

          // Periodically request data to ensure chunks are being collected
          dataRequestIntervalRef.current = setInterval(() => {
            if (mediaRecorderRef.current && mediaRecorderRef.current.state === 'recording') {
              mediaRecorderRef.current.requestData();
            }
          }, 1000);

          // Update progress based on audio time
          progressIntervalRef.current = setInterval(() => {
            if (audioDurationRef.current) {
              const elapsed = (Date.now() - startTimeRef.current) / 1000;
              const percentage = Math.min(Math.floor((elapsed / audioDurationRef.current) * 100), 99);
              setProgress(percentage);

              // Log data chunks periodically
              setDebug(`Progress: ${percentage}%, Chunks: ${chunksRef.current.length}, Duration: ${elapsed.toFixed(1)}s/${audioDurationRef.current.toFixed(1)}s`);

              // If we've been recording for a while but have no chunks, something is wrong
              if (elapsed > 3 && chunksRef.current.length === 0) {
                setDebug('Warning: No data chunks received after 3 seconds');
              }
            }
          }, 500);

          // Stop recording when audio ends
          audioElement.onended = () => {
            setDebug('Audio playback ended, stopping recording');

            // Clear the data request interval
            if (dataRequestIntervalRef.current) {
              clearInterval(dataRequestIntervalRef.current);
              dataRequestIntervalRef.current = null;
            }

            if (mediaRecorderRef.current && mediaRecorderRef.current.state === 'recording') {
              // Force one last dataavailable event before stopping
              mediaRecorderRef.current.requestData();

              // Small delay to ensure the last data is captured
              setTimeout(() => {
                try {
                  mediaRecorderRef.current.stop();
                } catch (stopError) {
                  console.error('Error stopping MediaRecorder:', stopError);
                  setDebug(`Error stopping recording: ${stopError.message}`);
                  setIsExporting(false);
                  cleanupResources();
                }
              }, 500);
            }
          };

        } catch (recorderError) {
          console.error('Error creating MediaRecorder:', recorderError);
          setDebug(`Failed to create MediaRecorder: ${recorderError.message}`);
          throw new Error(`Failed to create MediaRecorder: ${recorderError.message}. Try using a different browser.`);
        }
      } catch (audioError) {
        console.error('Error setting up audio:', audioError);
        setDebug(`Audio setup error: ${audioError.message}`);
        throw new Error(`Failed to set up audio: ${audioError.message}`);
      }

    } catch (error) {
      console.error("Error exporting video:", error);
      setDebug(`Export error: ${error.message || 'Unknown error'}`);
      alert(`Error exporting video: ${error.message}`);
      setIsExporting(false);
      cleanupResources();
    }
  };

  return (
    <div className="video-exporter">
      {isExporting ? (
        <div className="export-progress">
          <div className="progress-bar">
            <div className="progress-fill" style={{ width: `${progress}%` }}></div>
          </div>
          <div className="progress-text">{progress}% Exporting...</div>
          <div className="debug-info">{debug}</div>
        </div>
      ) : (
        <button
          className="export-button"
          onClick={exportVideo}
          disabled={!visualizerRef || !audioFile || isPlaying}
        >
          Export Video
        </button>
      )}
    </div>
  );
};

export default VideoExporter;
